create database KidsLand;
use KidsLand;

create table Personne (user_id INT AUTO_INCREMENT, user_name VARCHAR(20), user_age INT, user_pseudo VARCHAR(20), user_login VARCHAR(20), user_type VARCHAR(10), member_type VARCHAR(10), image VARCHAR(300), PRIMARY KEY (user_id));

create table Ride (id_ride INT AUTO_INCREMENT, name_ride VARCHAR(20), price_ride DOUBLE, features_ride VARCHAR(300), max_tickets INT, image VARCHAR(300) ,PRIMARY KEY (id_ride));

create table Command (id_command INT AUTO_INCREMENT, ride_command INT, user_command INT, adult_ticket INT, child_ticket INT, date_command Date, date_purchase Date, total_price DOUBLE, PRIMARY KEY (id_command), CONSTRAINT fk_ride_id FOREIGN KEY (ride_command) REFERENCES Ride(id_ride), CONSTRAINT fk_user_id FOREIGN KEY (user_command) REFERENCES Personne(user_id));
